# -*- coding: utf-8 -*-import timeimport speechimport subprocessimport win32com.clientcloseMainSystem =u"滚"listenMusic =u"我好累"Word =u"写文章"def callback(phrase, listener):    print(": %s"%phrase)    if phrase ==closeMainSystem:        speech.say(u"主人奴婢这就咕噜咕噜滚下去")        listener.stoplistening()        sys.exit()    elif phrase ==listenMusic:        speech.say(u"主人，奴婢这就为你来按摩")        webbrowser.open_new("http://douban.fm/")    elif phrase ==Word:        speech.say(u"主人我这就给您磨砚")        os.popen("D:\Program Files (x86)\Microsoft Office\Office15\2052\QuickStyles\word2013.dotx")listener =speech.listenforanything(callback)while listener.islistening():    text =input()    if text ==u"不要语音了":        listener.stoplistening()        sys.exit()    else:        speech.say(text)